function logIn(){localStorage.setItem("Logged in",1)}function setTheme(){null==localStorage.getItem("Theme")&&(window.matchMedia("(prefers-color-scheme: dark)").matches?localStorage.setItem("Theme","Dark"):localStorage.setItem("Theme","Light")),"Dark"==localStorage.getItem("Theme")?document.getElementById("current-style").setAttribute("href","styles/login-dark.css"):document.getElementById("current-style").setAttribute("href","styles/login-light.css")}window.addEventListener("load",setTheme());
