var video=document.querySelector("#videoElement");let canvas=document.querySelector("#canvas");function start(){navigator.mediaDevices.getUserMedia&&navigator.mediaDevices.getUserMedia({video:!0}).then(function(e){video.srcObject=e}).catch(function(e){console.log("Something went wrong!")})}function stop(e){canvas.getContext("2d").drawImage(video,0,0,canvas.width,canvas.height);var a=canvas.toDataURL("image/jpeg"),t=(a=atob(a.split("data:image/jpeg;base64,")[1])).length;imageBytes=new ArrayBuffer(t);for(var n=new Uint8Array(imageBytes),o=0;o<t;o++)n[o]=a.charCodeAt(o);var i=video.srcObject.getTracks();for(o=0;o<i.length;o++){i[o].stop()}video.srcObject=null}
